	<!--Numbers Animation-->
	<script>
		(function($) {
    $.fn.animateNumbers = function(stop, commas, duration, ease) {
        return this.each(function() {
            var $this = $(this);
            var start = parseInt($this.text().replace(/,/g, ""));
			commas = (commas === undefined) ? true : commas;
            $({value: start}).animate({value: stop}, {
            	duration: duration == undefined ? 1000 : duration,
            	easing: ease == undefined ? "swing" : ease,
            	step: function() {
            		$this.text(Math.floor(this.value));
					if (commas) { $this.text($this.text().replace(/(\d)(?=(\d\d\d)+(?!\d))/g, "$1,")); }
            	},
            	complete: function() {
            	   if (parseInt($this.text()) !== stop) {
            	       $this.text(stop);
					   if (commas) { $this.text($this.text().replace(/(\d)(?=(\d\d\d)+(?!\d))/g, "$1,")); }
            	   }
            	}
            });
        });
    };
})(jQuery);
$.fn.isOnScreen = function(){
    var viewport = {};
    viewport.top = $(window).scrollTop();
    viewport.bottom = viewport.top + $(window).height();
    var bounds = {};
    bounds.top = this.offset().top;
    bounds.bottom = bounds.top + this.outerHeight();
    return ((bounds.top <= viewport.bottom) && (bounds.bottom >= viewport.top));
};
function animatenumbers(){
    $(".getcount_rm").animateNumbers(20000, true, 1000);
    $(".getcount_tran").animateNumbers(10, false, 1000);
    $(".getcount_ngt").animateNumbers(80, false, 1000);
    $(".getcount_rev").animateNumbers(4000, true, 1000);
    $(".getcount_contry").animateNumbers(140, false, 1000);   
}
 // Number Animation
    if($('.us_container').isOnScreen()){
        animatenumbers();      
    }
    $(window).on('scroll', function(){
        if($('.us_container').isOnScreen()){
            animatenumbers();
        }
    });
